import React from "react";
import "../styles/Contact.css";


export default class Contact extends React.Component {
// state = {
//   name = "",
//   email = "",
//   phone = "",
//   message = ""
// }

// handleChange = event => {
//   this.setState({ name: event.target.value });
// }

// handleChange = event => {
