//       })
//   }

  render() {
    return (
      <div className="body_container">
      <div className="contact-form">
        <div className="container">
          <form id="contact" action="" onSubmit={this.handleSubmit}>
            <h3>Contact Form</h3>
            <h4 style={{ color: "orangered" }}>Hey Lets Work Together!</h4>
            <fieldset>
              <input
                placeholder="Your name"
                type="text"
                tabIndex={1}
                required
                autofocus
                onChange={this.handleChange}
              />
            </fieldset>
            <fieldset>
              <input
                placeholder="Your Email Address"
                type="email"
                tabIndex={2}
                required
                onChange={this.handleChange}
              />
            </fieldset>
            <fieldset>
              <input
                placeholder="Your Phone Number"
                type="tel"
                tabIndex={3}
                required
                onChange={this.handleChange}
              />
            </fieldset>
            <fieldset>
              <textarea
                placeholder="Type your message here...."
                tabIndex={5}
                required
                defaultValue={""}
                onChange={this.handleChange}
              />
            </fieldset>
            <fieldset>
              <button
                name="submit"
                type="submit"
                id="contact-submit"
                data-submit="...Sending"
              >
                Submit
            </button>
            </fieldset>
          </form>
        </div>
      </div>
    </div>
    )
  }
}
